export interface IBoundingClientRect {
    left: number;
    right: number;
    top: number;
    bottom: number;
    width: number;
    height: number;
    offsetLeft?: number;
    offsetTop?: number;
}
/**
 * Get bounding client rect of given element
 * @method
 * @memberof Popper.Utils
 * @param {HTMLElement} element
 * @return {Object} client rect
 */
export default function getBoundingClientRect(element: HTMLElement): IBoundingClientRect;
